insert into `gs_article`(`id`,`cat_id`,`title`,`content`,`author`,`keywords`,`article_type`,`is_open`,`add_time`,`litpic`,`description`,`rank`) values('13','4','视觉与交互设计师','<p>岗位职责<br/></p><p>1、负责网站和移动页面的视觉设计和交互设计;</p><p>2、参与用户研究、趋势分析，完成界面交互行为和功能的改良，提高产品的易用性;</p><p>3、部分平面宣传册设计，为部门的运营活动提供设计支持。</p><p>4、规范产品在用户体验上的统一性和一致性，编写、维护和更新产品的相关设计文档和规范;</p><p>任职资格</p><p>1、美术基础扎实，擅长平面设计、网页设计，精通设计软件的使用;</p><p>2、有3年以上APP、Web端交互设计工作经验，有成功线上案例;</p><p>3、具备良好的沟通和协作能力，能与产品经理配合默契;</p><p>4、富于创新、思维灵活，并对先进的设计理念和技术有较强的理解能力;</p><p>其他：</p><p>1、对生命健康领域感兴趣者优先;</p><p>2、请附个人以往作品，包括但不限于网页设计作品。</p><p>薪资待遇</p><p>表现突出者，年薪高达15W</p><p><br/></p><p>简历投递</p><p>邮箱：422621572@qq.com</p><p>联系人：江燕</p><p><br/></p>','','','0','1','1465980880','','岗位职责1、负责网站和移动页面的视觉设计和交互设计;2、参与用户研究、趋势分析，完成界面交互行为和功能的改良，提高产品的','50');
insert into `gs_article`(`id`,`cat_id`,`title`,`content`,`author`,`keywords`,`article_type`,`is_open`,`add_time`,`litpic`,`description`,`rank`) values('14','4','产品经理','<p>岗位职责<br/></p><p>1、负责平台产品规划、业务流程设计、功能设计，推动产品研发整个过程</p><p>2、制定产品需求计划，进行可行性分析及设计，撰写产品功能需求说明书，负责需求的跟踪和控制;</p><p>3、研究并分析竞争对手，把握产品和运营现状调整优化产品策略等;</p><p>4、确保产品实现进度和质量，跟踪产品的设计、开发、发布、运营等环节，确保项目按时完成和上线。</p><p>任职资格</p><p>1、五年以上互联网产品设计工作经验，熟悉云计算技术和产品特点;</p><p>2、具备独立负责的产品设计和规划经验，以及复杂产品的规划和设计能力，良好的需求分析、挖掘能力，优秀的产品设计能力;</p><p>3、较强的用户需求判断、引导、控制能力，有很好的合作能力，善于合作协调沟通，思维敏捷，具有非常好的书面和口头表达能力;</p><p>4、性格开朗，善于沟通，有较强的管理及执行能力，良好的团队合作精神，出色的跨部门协调和问题解决能力，能承担较大的压力。</p><p>其他：</p><p>1、了解常规基因测序数据处理流程优先;</p><p>2、有云产品研发经验优先。</p><p>薪资待遇</p><p>表现突出者，年薪高达25W</p><p><br/></p><p>简历投递</p><p>邮箱：422621572@qq.com</p><p>联系人：江燕</p><p><br/></p><p><br/></p>','','','0','1','1465980970','','岗位职责1、负责平台产品规划、业务流程设计、功能设计，推动产品研发整个过程2、制定产品需求计划，进行可行性分析及设计，撰','50');
