insert into `gs_article`(`id`,`cat_id`,`title`,`content`,`author`,`keywords`,`article_type`,`is_open`,`add_time`,`litpic`,`description`,`rank`) values('23','4','产品运营经理','<p>岗位职责<br/></p><p>1、根据平台的业绩目标，制定运营规划，协调各部门资源，确保运营目标的实现;</p><p>2、负责平台的内外部推广，拓展产品用户数，提升用户活跃度;</p><p>3、数据收集与分析，根据效果推动产品的改进与营销策略的改变;</p><p>4、负责社区的维护;</p><p>任职资格</p><p>1、5年以上生物信息分析或产品经理岗位工作经验，熟悉科服或医学产品、了解华大业务流程的优先;</p><p>2、对IT、云计算、互联网产品的运营有较好的理解，能够准确把握产品的核心及用户的心理特点，并能有针对性设计产品包装方案;</p><p>3、具有组织策划能力及高度的执行力，有组织或参与学术交流、展会等相关工作经验者优先;</p><p>4、良好的写作和表达能力，具有创造力的思维，善于用数据、故事等阐述和表达观点;</p><p>5、擅长协调沟通，能推进跨部门和跨公司的合作。</p><p>薪资待遇</p><p>表现突出者，年薪高达25W</p><p><br/></p><p>简历投递</p><p>邮箱：422621572@qq.com</p><p>联系人：江燕</p><p><br/></p>','','','0','1','1465981993','','岗位职责1、根据平台的业绩目标，制定运营规划，协调各部门资源，确保运营目标的实现;2、负责平台的内外部推广，拓展产品用户','50');
