insert into `gs_goodspo`(`id`,`title`,`gid`,`pdesc`,`createtime`,`thumb`,`ishidden`) values('11','记忆能力','2','<p><span style=\";font-family:宋体;font-size:14px\"><span style=\"font-family:宋体\">记忆力是人类的一种心智活动。记忆潜能是多种能力的发展的基础，记忆潜能不同，其后天记忆能力也有所不同。记忆包括</span></span><span style=\";font-family:宋体;color:rgb(255,0,0);font-size:14px\"><span style=\"font-family:宋体\">（我们检测的项目）</span></span><span style=\";font-family:宋体;font-size:14px\"><span style=\"font-family:宋体\">长时记忆和工作记忆（短期记忆），不同的记忆能力直接影响到孩子不同的学习能力。</span></span></p><p><br/></p>','0000-00-00 00:00:00','/uploads/16/09/14/09141041563639.png','0');
insert into `gs_goodspo`(`id`,`title`,`gid`,`pdesc`,`createtime`,`thumb`,`ishidden`) values('12','领导决策能力','2','<p><span style=\";font-family:宋体;font-size:14px\">&nbsp;<span style=\"font-family:宋体\">一个人是否具有领导能力，他的决策能力起到了至关重要的作用。</span></span></p><p><br/></p>','0000-00-00 00:00:00','/uploads/16/09/14/09141042277440.png','0');
insert into `gs_goodspo`(`id`,`title`,`gid`,`pdesc`,`createtime`,`thumb`,`ishidden`) values('13','逻辑思维能力','2','<p style=\"margin-left:24px\"><span style=\"font-family:微软雅黑;font-size:16px\">&nbsp;</span><span style=\";font-family:微软雅黑;font-size:15px\"><span style=\"font-family:微软雅黑\">逻辑思维能力</span><span style=\"font-family:微软雅黑\">是人类对于事物观察、比较、分析、抽象、概括、判断、推理的能力。逻辑推理能力强的人思想活跃，思路清晰。</span></span></p><p><br/></p>','0000-00-00 00:00:00','/uploads/16/09/14/09141042545841.png','0');
insert into `gs_goodspo`(`id`,`title`,`gid`,`pdesc`,`createtime`,`thumb`,`ishidden`) values('14','数学能力','2','<p><span style=\";font-family:宋体;font-size:14px\"><span style=\"font-family:宋体\">数学能力主要由以下几个重要因素决定</span></span><span style=\";font-family:宋体;color:rgb(255,0,0);font-size:14px\"><span style=\"font-family:宋体\">（我们检测的项目）</span></span><span style=\";font-family:宋体;font-size:14px\">: <span style=\"font-family:宋体\">对数字的敏感度，数学运算能力，逻辑推理能力等。</span></span></p><p><br/></p>','0000-00-00 00:00:00','/uploads/16/09/14/09141043212645.png','0');
insert into `gs_goodspo`(`id`,`title`,`gid`,`pdesc`,`createtime`,`thumb`,`ishidden`) values('15','舞蹈特长','2','<p style=\"margin-left:24px\"><span style=\";font-family:微软雅黑;font-size:15px\"><span style=\"font-family:微软雅黑\">舞蹈是一个需要长期坚持的一项活动。它不仅需要孩子具备持之以恒的坚持力</span></span><span style=\";font-family:微软雅黑;color:rgb(255,0,0);font-size:15px\"><span style=\"font-family:微软雅黑\">（我们检测的项目）</span></span><span style=\";font-family:微软雅黑;font-size:15px\"><span style=\"font-family:微软雅黑\">，还需要</span></span><span style=\";font-family:微软雅黑;color:rgb(255,0,0);font-size:15px\"><span style=\"font-family:微软雅黑\">（我们检测的项目）</span></span><span style=\";font-family:微软雅黑;font-size:15px\"><span style=\"font-family:微软雅黑\">良好的体型支撑，体能充沛，柔韧的身体素质，优秀的音乐感知力，舞蹈动作的情节记忆能力等。</span></span></p><p><br/></p>','0000-00-00 00:00:00','/uploads/16/09/14/09141043533309.png','0');
insert into `gs_goodspo`(`id`,`title`,`gid`,`pdesc`,`createtime`,`thumb`,`ishidden`) values('16','学习能力','2','<p style=\"margin-left:24px\"><span style=\";font-family:微软雅黑;font-size:15px\"><span style=\"font-family:微软雅黑\">学习能力是一个复杂的能力体系</span>, <span style=\"font-family:微软雅黑\">构成学习能力的主要要素包括</span></span><span style=\";font-family:微软雅黑;color:rgb(255,0,0);font-size:15px\"><span style=\"font-family:微软雅黑\">（我们检测的项目）</span></span><span style=\";font-family:微软雅黑;font-size:15px\"><span style=\"font-family:微软雅黑\">：记忆能力，理解能力，逻辑思维能力，动手能力等。</span></span></p><p><br/></p>','0000-00-00 00:00:00','/uploads/16/09/14/09141044391680.png','0');
insert into `gs_goodspo`(`id`,`title`,`gid`,`pdesc`,`createtime`,`thumb`,`ishidden`) values('17','音乐特长','2','<p style=\"margin-left:24px\"><span style=\";font-family:微软雅黑;font-size:15px\"><span style=\"font-family:微软雅黑\">出色的音乐家大多具备以下天赋特征</span></span><span style=\";font-family:微软雅黑;color:rgb(255,0,0);font-size:15px\"><span style=\"font-family:微软雅黑\">（我们检测的项目）</span></span><span style=\";font-family:微软雅黑;font-size:15px\"><span style=\"font-family:微软雅黑\">：优良的听力，优秀的音乐感知力，优美而准确的发音能力，乐器的娴熟操作能力，音乐方面的创造力。</span></span></p><p><br/></p>','0000-00-00 00:00:00','/uploads/16/09/14/09141045162382.png','0');
insert into `gs_goodspo`(`id`,`title`,`gid`,`pdesc`,`createtime`,`thumb`,`ishidden`) values('18','运动特长','2','<p><span style=\";font-family:宋体;font-size:14px\"><span style=\"font-family:宋体\">一个优秀的运动员不仅要勤加练习，而且必须具备一定的先天优势</span></span><span style=\";font-family:宋体;color:rgb(255,0,0);font-size:14px\"><span style=\"font-family:宋体\">（我们检测的项目）</span></span><span style=\";font-family:宋体;font-size:14px\"><span style=\"font-family:宋体\">：短距离运动的爆发力，长距离运动的耐力，充沛的体能支持，持之以恒的坚持力。</span></span></p><p><br/></p>','0000-00-00 00:00:00','/uploads/16/09/14/09141045425598.png','0');
insert into `gs_goodspo`(`id`,`title`,`gid`,`pdesc`,`createtime`,`thumb`,`ishidden`) values('19','专注能力','2','<p style=\"margin-left:24px\"><span style=\";font-family:微软雅黑;font-size:15px\"><span style=\"font-family:微软雅黑\">注意力是心理活动指向和集中于物或事的能力。注意力受到大脑节律因子的调控。专注于某一事情，才能集中精力的深入思考，事半功倍地完成任务。</span></span></p><p><br/></p>','0000-00-00 00:00:00','/uploads/16/09/14/09141046156886.png','0');
insert into `gs_goodspo`(`id`,`title`,`gid`,`pdesc`,`createtime`,`thumb`,`ishidden`) values('20','自律能力','2','<p><span style=\";font-family:宋体;font-size:14px\"><span style=\"font-family:宋体\">自律是人类抵制不良习惯和环境的能力。孩子的成长过程中，会遇到各种不良环境的干扰，如网络游戏，黄色暴力文化，毒品等。一个具有很强自律能力的孩子更易在复杂的环境中成材。</span></span></p><p><br/></p>','0000-00-00 00:00:00','/uploads/16/09/14/09141046425777.png','0');
